<?php// Constant for database connectiondefine('DATABASE_HOST', '127.0.0.1');define('DATABASE_USERNAME', 'root');define('DATABASE_PASSWORD', '');define('DATABASE_NAME', 'assignment');// Require concrete class that extends Row_Abstractrequire_once('concrete.php');// Construct new instance of the concrete class$rowObject = new DB_Concrete(DATABASE_HOST ,DATABASE_USERNAME, DATABASE_PASSWORD,DATABASE_NAME);// Populate the object$rowObject->setFromArray(array(    'username' => 'test2',    'password' => md5('helloWorld2'),    'name' => 'Test Account2'));// Save the row to the database$rowObject->save();